function efsrRandomNo($length = 5) {
	$characters = '012345';
	$charactersLength = strlen($characters);
	$randomString = '';
	for ($i = 0; $i < $length; $i++){$randomString .= $characters[rand(0, $charactersLength - 1)];}
	return $randomString;
}
